import client from '../../client'

const Post = (props) => {
    const router = useRouter()

    return (
        <div>
            <h1>{router.query.slug}</h1>
        </div>
    )
}

Post.getInitialProps = async function(context) {
    const { slug = '' } = context.query
    return await client.fetch(`*[_type == "post" && slug.current == $slug][0]`, { slug })
}

export default Post
