import client from '../../client'

const Post = (props) => {
    const { title = 'Missing title', name = 'Missing name' } = props
    return (
        <div>
            <h1>{title}</h1>
            <span>By {name}</span>
        </div>
    )
}

Post.getInitialProps = async function(context) {
    const { slug = '' } = context.query
    return await client.fetch(`
        *[_type == "post" && slug.current == $slug][0]{
            title,
            "name":author->name
        }
    `, { slug })
}

export default Post
